import asyncio
import os
import logging
from typing import Dict, Any, List, Optional

import httpx
from mcp.server.fastmcp import FastMCP

# Configure logging
logging.basicConfig(level=logging.INFO)
logger = logging.getLogger("weather-server")

# Weather API configuration
WEATHER_API_BASE_URL = "https://api.weatherapi.com/v1"
WEATHER_API_KEY = os.getenv("WEATHER_API_KEY", "")
MAX_DAYS_FORECAST = 3

# Create FastMCP server instance
mcp = FastMCP(name="Weather MCP Server")

# Global HTTP client
http_client = httpx.AsyncClient(timeout=30.0)


# -----------------------
# Tools (functions exposed to the client)
# -----------------------


@mcp.tool()
async def get_current_weather(location: str) -> str:
  """
  Get current weather information for a location.

  Args:
    location: The city name, coordinates, or location to get weather for

  Returns:
    Current weather information including temperature, conditions, and more
  """
  if not WEATHER_API_KEY:
    return (
      "Error: WEATHER_API_KEY environment variable not set. Please set your weatherapi.com API key."
    )

  try:
    params = {"key": WEATHER_API_KEY, "q": location, "aqi": "yes"}  # Include air quality data

    url = f"{WEATHER_API_BASE_URL}/current.json"
    response = await http_client.get(url, params=params)
    response.raise_for_status()

    data = response.json()

    # Format the weather data
    current = data["current"]
    location_info = data["location"]

    weather_info = f"""ðŸŒ¤ï¸ **Current Weather for {location_info['name']}, {location_info['country']}**
          ðŸ“ **Location**: {location_info['name']}, {location_info['region']}, {location_info['country']}
          ðŸ•’ **Local Time**: {location_info['localtime']}
          ðŸŒ¡ï¸ **Temperature**: {current['temp_c']}Â°C ({current['temp_f']}Â°F)
          ðŸŒ¡ï¸ **Feels Like**: {current['feelslike_c']}Â°C ({current['feelslike_f']}Â°F)
          â˜ï¸ **Condition**: {current['condition']['text']}
          ðŸ’¨ **Wind**: {current['wind_kph']} km/h ({current['wind_mph']} mph) {current['wind_dir']}
          ðŸ’§ **Humidity**: {current['humidity']}%
          ðŸ‘ï¸ **Visibility**: {current['vis_km']} km ({current['vis_miles']} miles)
          ðŸŒŠ **Pressure**: {current['pressure_mb']} mb ({current['pressure_in']} in)
          â˜” **Precipitation**: {current['precip_mm']} mm ({current['precip_in']} in)
          ðŸŒ¡ï¸ **Heat Index**: {current['heatindex_c']}Â°C ({current['heatindex_f']}Â°F)
          â„ï¸ **Wind Chill**: {current['windchill_c']}Â°C ({current['windchill_f']}Â°F)
          â˜€ï¸ **UV Index**: {current['uv']}
        """

    # Add air quality if available
    if "air_quality" in data:
      aqi = data["air_quality"]
      weather_info += f"\nðŸŒ«ï¸ **Air Quality**:\n"
      weather_info += f"   â€¢ CO: {aqi.get('co', 'N/A')} Î¼g/mÂ³\n"
      weather_info += f"   â€¢ NO2: {aqi.get('no2', 'N/A')} Î¼g/mÂ³\n"
      weather_info += f"   â€¢ O3: {aqi.get('o3', 'N/A')} Î¼g/mÂ³\n"
      weather_info += f"   â€¢ PM2.5: {aqi.get('pm2_5', 'N/A')} Î¼g/mÂ³\n"
      weather_info += f"   â€¢ PM10: {aqi.get('pm10', 'N/A')} Î¼g/mÂ³\n"
      weather_info += f"   â€¢ SO2: {aqi.get('so2', 'N/A')} Î¼g/mÂ³\n"

    return weather_info

  except httpx.HTTPStatusError as e:
    error_msg = f"HTTP Error {e.response.status_code}: {e.response.text}"
    return f"Error fetching weather data: {error_msg}"
  except Exception as e:
    return f"Error fetching weather data: {str(e)}"


@mcp.tool()
async def get_weather_forecast(location: str, days: int = 3) -> str:
  """
  Get weather forecast for a location.

  Args:
    location: The city name, coordinates, or location to get forecast for
    days: Number of days to forecast (1-3, default: 3)

  Returns:
    Weather forecast information for the specified number of days
  """
  if not WEATHER_API_KEY:
    return (
      "Error: WEATHER_API_KEY environment variable not set. Please set your weatherapi.com API key."
    )

  # Validate days parameter
  if days < 1 or days > MAX_DAYS_FORECAST:
    return f"Error: Days parameter must be between 1 and {MAX_DAYS_FORECAST}"

  try:
    params = {"key": WEATHER_API_KEY, "q": location, "days": days, "aqi": "yes", "alerts": "yes"}

    url = f"{WEATHER_API_BASE_URL}/forecast.json"
    response = await http_client.get(url, params=params)
    response.raise_for_status()

    data = response.json()

    # Format the forecast data
    location_info = data["location"]
    forecast_info = (
      f"ðŸŒ¤ï¸ **Weather Forecast for {location_info['name']}, {location_info['country']}**\n\n"
    )

    for day in data["forecast"]["forecastday"]:
      date = day["date"]
      day_data = day["day"]

      forecast_info += f"ðŸ“… **{date}**\n"
      forecast_info += f"   ðŸŒ¡ï¸ **Temperature**: {day_data['mintemp_c']}Â°C to {day_data['maxtemp_c']}Â°C ({day_data['mintemp_f']}Â°F to {day_data['maxtemp_f']}Â°F)\n"
      forecast_info += (
        f"   ðŸŒ¡ï¸ **Avg Temperature**: {day_data['avgtemp_c']}Â°C ({day_data['avgtemp_f']}Â°F)\n"
      )
      forecast_info += f"   â˜ï¸ **Condition**: {day_data['condition']['text']}\n"
      forecast_info += (
        f"   ðŸ’¨ **Max Wind**: {day_data['maxwind_kph']} km/h ({day_data['maxwind_mph']} mph)\n"
      )
      forecast_info += f"   ðŸ’§ **Humidity**: {day_data['avghumidity']}%\n"
      forecast_info += (
        f"   ðŸ‘ï¸ **Visibility**: {day_data['avgvis_km']} km ({day_data['avgvis_miles']} miles)\n"
      )
      forecast_info += f"   â˜” **Precipitation**: {day_data['totalprecip_mm']} mm ({day_data['totalprecip_in']} in)\n"
      forecast_info += f"   â˜€ï¸ **UV Index**: {day_data['uv']}\n"
      forecast_info += f"   ðŸŒ… **Sunrise**: {day['astro']['sunrise']}\n"
      forecast_info += f"   ðŸŒ‡ **Sunset**: {day['astro']['sunset']}\n"
      forecast_info += f"   ðŸŒ™ **Moon Phase**: {day['astro']['moon_phase']}\n\n"

    # Add alerts if available
    if "alerts" in data and data["alerts"]["alert"]:
      forecast_info += "âš ï¸ **Weather Alerts**:\n"
      for alert in data["alerts"]["alert"]:
        forecast_info += f"   â€¢ **{alert['headline']}**: {alert['desc']}\n"

    return forecast_info

  except httpx.HTTPStatusError as e:
    error_msg = f"HTTP Error {e.response.status_code}: {e.response.text}"
    return f"Error fetching forecast data: {error_msg}"
  except Exception as e:
    return f"Error fetching forecast data: {str(e)}"


@mcp.tool()
async def search_locations(query: str) -> str:
  """
  Search for locations/cities matching a query.

  Args:
    query: The search query (city name, coordinates, etc.)

  Returns:
    List of matching locations with their details
  """
  if not WEATHER_API_KEY:
    return (
      "Error: WEATHER_API_KEY environment variable not set. Please set your weatherapi.com API key."
    )

  try:
    params = {"key": WEATHER_API_KEY, "q": query}

    url = f"{WEATHER_API_BASE_URL}/search.json"
    response = await http_client.get(url, params=params)
    response.raise_for_status()

    data = response.json()

    if not data:
      return f"No locations found matching '{query}'"

    # Format the search results
    search_info = f"ðŸ” **Search Results for '{query}'**\n\n"

    for i, location in enumerate(data, 1):
      search_info += f"**{i}. {location['name']}**\n"
      search_info += f"   ðŸ“ **Region**: {location['region']}\n"
      search_info += f"   ðŸŒ **Country**: {location['country']}\n"
      search_info += f"   ðŸ“ **Coordinates**: {location['lat']}, {location['lon']}\n"
      if "url" in location:
        search_info += f"   ðŸ”— **URL**: {location['url']}\n"
      search_info += "\n"

    return search_info

  except httpx.HTTPStatusError as e:
    error_msg = f"HTTP Error {e.response.status_code}: {e.response.text}"
    return f"Error searching locations: {error_msg}"
  except Exception as e:
    return f"Error searching locations: {str(e)}"


# Log tool registration
logger.info("Weather tools registered: get_current_weather, search_locations")

