# -----------------------
# Cleanup on exit
# -----------------------


async def cleanup():
  """Clean up resources on exit."""
  await http_client.aclose()


if __name__ == "__main__":
  logger.info(f"\n--- Starting {mcp} via __main__ ---")
  try:
    mcp.run(transport="stdio")
  finally:
    # Clean up HTTP client
    asyncio.run(cleanup())
